# Auto convert input properties to local Powershell variables

# Parsing JSON elements into Powershell object
function ParseJsonProps ($jsonFile)
{
 $jsonjsonCharCount = 0
 $newObjStart = $false
 $arrayStart = $false
 $tokenStart = $false
 $valueStart = $false
 $escapeStart = $false

 $jsonFileLen = $jsonFile.length
 $jsonObj = New-Object System.Text.StringBuilder

 ForEach($c in $jsonFile.ToCharArray()) {
    $jsonCharCount++
    $tmpStr = ""
    switch -regex ($c) {
        "{" { 
            if($newObjStart -eq $false) {
                $tmpStr = "(New-Object PSObject "
                $newObjStart = $true
            } else {
                $tmpStr = $c
            }
        }
        "}" {
            if($jsonCharCount -lt $jsonFileLen) {
                $tmpStr = $c
            } else {
                $tmpStr = ")"
            }
        }
        '"' {
            if($tokenStart -eq $false -and $valueStart -eq $false -and $arrayStart -eq $false) {
                $tmpStr = '| Add-Member -Passthru NoteProperty "'
            } else {
                if($escapeStart -eq $false) {
                    $tmpStr = $c
                } else {
                    $tmpStr = "```""
                    $escapeStart = $false
                }
            }
            $tokenStart = $true
        }
        ":" {
            if($valueStart -eq $true -or $arrayStart -eq $true) {
                $tmpStr = $c
            } else {
                $tmpStr = " "
            }
            $valueStart = $true
        }
        "," {
            if($arrayStart) {
                $tmpStr = $c
            } else {
                $valueStart = $false
                $tokenStart = $false
            }
        }	
        "\[" {
            $tmpStr = "@("
            $arrayStart = $true
        }
        "\]" {
            $tmpStr = ")"
            $arrayStart = $false
        }
        "``" {
            $tmpStr = "````"
        }
        "\\" {
            if($escapeStart -eq $false) {
                $escapeStart = $true
            } else {
                $tmpStr = $c
                $escapeStart = $false
            }
        }
        "\$" {
            if($escapeStart -eq $false) {
                $tmpStr = '`$'
            } else {
                $tmpStr = '`$'
                $escapeStart = $false
            }
        }
        "n" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``n"
                $escapeStart = $false
            }
        }
        "r" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``r"
                $escapeStart = $false
            }
        }
        "t" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``t"
                $escapeStart = $false
            }
        }
        "b" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``b"
                $escapeStart = $false
            }
        }
        default {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "\$c"
                $escapeStart = $false
            }
            #$tmpStr = $c
            #if($escapeStart -eq $true) {
            #    $escapeStart = $false
            #}
        }
    }                        
    [void] $jsonObj.Append($tmpStr)
 }

 $jsonObj | Invoke-Expression
}

$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = ParseJsonProps $vmw_appd_json_file
$vmw_appd_json_props = $vmw_appd_json_obj.PsObject.Properties

# Assigning obj to local variable
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 #$vmw_appd_obj_prop_name_full = '$vmw_appd_json_obj' + '.' + $vmw_appd_obj_prop_name
 #$vmw_appd_obj_prop_value = Invoke-Expression $vmw_appd_obj_prop_name_full
 Set-Variable -Name $vmw_appd_obj_prop_name -value $vmw_appd_json_obj."$vmw_appd_obj_prop_name"
}


# Execute user's Powershell script
$error.clear()
$global:LASTEXITCODE = $null
. "$($Args[0])" 2>&1 | out-file -encoding Default "$($Args[3])"

if ($LASTEXITCODE -and $LASTEXITCODE -ne 0) {
 "`nABORT. LASTEXITCODE is non-zero.`n" | out-file -encoding Default -append $($Args[3])
 exit $LASTEXITCODE
}

if ($error.count -gt 0) {
 "`nABORT. Encountered error in Powershell.`n" | out-file -encoding Default -append $($Args[3])
 exit $error.count
 }

# Export Powershell variables to output properties file
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

ns7:component-version-script.script:
# - Download AutoSPInstaller packages and Sharepoint installer packages # - Download PsExec package # - Download Adobe pdf files for SharePoint installation # # For the best performance, It is better to pre-downloaded those two packages # in the VM template. It will save quite an amount of deployment time. # # Properites Required: # - auto_spinstaller_url # - sp_download_url # - sp_download_location # - autoSPInstaller_file # - sp_installer_file # - psexec_url # - adobe_pdf_iFiler_url # - adobe_pdf_icon_url ##########################################################################
# VALIDATION FUNCTIONS Function check_error { if ($error.count -gt 0) { error_exit $args[0] } }
Function error_exit { echo $args[0] exit 1 }
Function CreateDir([string]$dirName) { If (!(Test-Path "$dirName" -Verbose)) { Write-Output "create download directory $dirName" New-Item -ItemType Directory $dirName -ErrorAction SilentlyContinue } }
Function DeleteDir([string]$dirName) { If (Test-Path "$dirName" -Verbose) { Remove-Item -Recurse -Force $dirName } }
## Function: Download a package from repos to the local location Function Download-Package ([string]$from, [string]$to) { $file=$from.Split('/')[-1] Write-Output "DOWNLOADING `"$file`" PACKAGE FROM `"$from`" to $to..." $error.clear() $wc.Downloadfile("$from","$to\$file") check_error "ERROR:WHILE DOWNLOADING `"$file`" PACKAGE" Write-Output "DOWNLOADING `"$file`" PACKAGE IS DONE." }
## Function: Download a package from repos to the local location Function Download-Package ([string]$from, [string]$to, [string]$dfile) { Write-Output "DOWNLOADING `"$dfile`" PACKAGE FROM `"$from`" to $to..." $error.clear() $wc.Downloadfile("$from","$to\$dfile") check_error "ERROR:WHILE DOWNLOADING `"$dfile`" PACKAGE" Write-Output "DOWNLOADING `"$dfile`" PACKAGE IS DONE." }
# NOTE: THE ".NET Framework 3.5 SP1 and Windows Installer 4.5" ARE REQUIRED COMPONENTS TO INSTALL SQL2008/2012 # INSTALL PREREQUISITES .NET Framework 3.5 SP1 AND Windows Installer 4.5 Write-Output "INSTALLING PRE-REQUISITES - .NET Framework 3.5 SP1 AND Windows Installer 4.5..." $error.clear() Servermanagercmd -install Application-Server check_error "ERROR:WHILE INSTALLING APPLICATION SERVER FEATURE" $error.clear() Servermanagercmd -install AS-Dist-Transaction AS-Ent-Services -allSubFeatures check_error "ERROR:WHILE INSTALLING PRE-REQUISITES" Write-Output "PRE-REQUISITES `'.NET Framework 3.5 SP1 AND Windows Installer 4.5`' INSTALLED SUCCESSFULLY"
# DOWNLOAD THE SP INSTALLER and AutoSPInstaller INTO THE DIRECTORY "c:\Download" CreateDir $sp_download_location $wc = new-object System.Net.WebClient Download-Package -From "$auto_spinstaller_url" -To "$sp_download_location" -DFile "$autoSPInstaller_file" Download-Package -From "$sp_download_url" -To "$sp_download_location" -DFile "$sp_installer_file"
# DOWNLOAD PSEXEC package $psexec_file=$psexec_url.Split('/')[-1] Download-Package -From "$psexec_url" -To "$sp_download_location" -DFile "$psexec_file"
# DOWNLOAD PDF PACKAGES $pdf_file=$adobe_pdf_iFiler_url.Split('/')[-1] Download-Package -From "$adobe_pdf_iFiler_url" -To "$sp_download_location" -Dfile "$pdf_file" $icon_file=$adobe_pdf_icon_url.Split('/')[-1] Download-Package -From "$adobe_pdf_icon_url" -To "$sp_download_location" -Dfile "$icon_file"

supported-os-details.exec-script:
$error.clear()
# Auto convert input properties to local Powershell variables

# Parsing JSON elements into Powershell object
function ParseJsonProps ($jsonFile)
{
 $jsonjsonCharCount = 0
 $newObjStart = $false
 $arrayStart = $false
 $tokenStart = $false
 $valueStart = $false
 $escapeStart = $false

 $jsonFileLen = $jsonFile.length
 $jsonObj = New-Object System.Text.StringBuilder

 ForEach($c in $jsonFile.ToCharArray()) {
    $jsonCharCount++
    $tmpStr = ""
    switch -regex ($c) {
        "{" { 
            if($newObjStart -eq $false) {
                $tmpStr = "(New-Object PSObject "
                $newObjStart = $true
            } else {
                $tmpStr = $c
            }
        }
        "}" {
            if($jsonCharCount -lt $jsonFileLen) {
                $tmpStr = $c
            } else {
                $tmpStr = ")"
            }
        }
        '"' {
            if($tokenStart -eq $false -and $valueStart -eq $false -and $arrayStart -eq $false) {
                $tmpStr = '| Add-Member -Passthru NoteProperty "'
            } else {
                if($escapeStart -eq $false) {
                    $tmpStr = $c
                } else {
                    $tmpStr = "```""
                    $escapeStart = $false
                }
            }
            $tokenStart = $true
        }
        ":" {
            if($valueStart -eq $true -or $arrayStart -eq $true) {
                $tmpStr = $c
            } else {
                $tmpStr = " "
            }
            $valueStart = $true
        }
        "," {
            if($arrayStart) {
                $tmpStr = $c
            } else {
                $valueStart = $false
                $tokenStart = $false
            }
        }	
        "\[" {
            $tmpStr = "@("
            $arrayStart = $true
        }
        "\]" {
            $tmpStr = ")"
            $arrayStart = $false
        }
        "``" {
            $tmpStr = "````"
        }
        "\\" {
            if($escapeStart -eq $false) {
                $escapeStart = $true
            } else {
                $tmpStr = $c
                $escapeStart = $false
            }
        }
        "\$" {
            if($escapeStart -eq $false) {
                $tmpStr = '`$'
            } else {
                $tmpStr = '`$'
                $escapeStart = $false
            }
        }
        "n" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``n"
                $escapeStart = $false
            }
        }
        "r" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``r"
                $escapeStart = $false
            }
        }
        "t" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``t"
                $escapeStart = $false
            }
        }
        "b" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``b"
                $escapeStart = $false
            }
        }
        default {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "\$c"
                $escapeStart = $false
            }
            #$tmpStr = $c
            #if($escapeStart -eq $true) {
            #    $escapeStart = $false
            #}
        }
    }                        
    [void] $jsonObj.Append($tmpStr)
 }

 $jsonObj | Invoke-Expression
}

$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = ParseJsonProps $vmw_appd_json_file
$vmw_appd_json_props = $vmw_appd_json_obj.PsObject.Properties

# Assigning obj to local variable
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 #$vmw_appd_obj_prop_name_full = '$vmw_appd_json_obj' + '.' + $vmw_appd_obj_prop_name
 #$vmw_appd_obj_prop_value = Invoke-Expression $vmw_appd_obj_prop_name_full
 Set-Variable -Name $vmw_appd_obj_prop_name -value $vmw_appd_json_obj."$vmw_appd_obj_prop_name"
}


# Execute user's Powershell script
$error.clear()
$global:LASTEXITCODE = $null
. "$($Args[0])" 2>&1 | out-file -encoding Default "$($Args[3])"

if ($LASTEXITCODE -and $LASTEXITCODE -ne 0) {
 "`nABORT. LASTEXITCODE is non-zero.`n" | out-file -encoding Default -append $($Args[3])
 exit $LASTEXITCODE
}

if ($error.count -gt 0) {
 "`nABORT. Encountered error in Powershell.`n" | out-file -encoding Default -append $($Args[3])
 exit $error.count
 }

# Export Powershell variables to output properties file
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

